import { defineNuxtModule, createResolver, installModule, addComponent } from '@nuxt/kit';

const module = defineNuxtModule({
  meta: {
    name: "nuxt-icon",
    configKey: "icon",
    compatibility: {
      nuxt: "^3.0.0-rc.9"
    }
  },
  defaults: {},
  setup(_options, nuxt) {
    const { resolve } = createResolver(import.meta.url);
    nuxt.hook("schema:extend", (schemas) => {
      schemas.push({
        appConfig: {
          nuxtIcon: {
            $schema: {
              title: "Nuxt Icon",
              description: "Configure the defaults of Nuxt Icon"
            },
            size: {
              $default: "",
              $schema: {
                title: "Icon Size",
                description: "Set the default icon size",
                tags: ["@studioIcon material-symbols:format-size-rounded"]
              }
            },
            class: {
              $default: "",
              $schema: {
                title: "CSS Class",
                description: "Set the default CSS class",
                tags: ["@studioIcon material-symbols:css"]
              }
            },
            aliases: {
              $default: {},
              $schema: {
                title: "Icon aliases",
                description: "Define Icon aliases to update them easily without code changes.",
                tags: [
                  "@studioIcon material-symbols:star-rounded",
                  "@studioInputObjectValueType icon"
                ],
                tsType: "{ [alias: string]: string }"
              }
            }
          }
        }
      });
    });
    installModule("nuxt-config-schema");
    addComponent({
      name: "Icon",
      global: true,
      filePath: resolve("./runtime/Icon.vue")
    });
  }
});

export { module as default };
